bool Google()
{
    return
        RewindUnless(()=>
            text[p++] == 'g' &&
                Repeat(2, 100,
                    RewindUnless(()=>
                        text[p++] == 'o'
                    )
                )
            text[p++] == 'g' &&
            text[p++] == 'l' &&
            text[p++] == 'e'
        );
}

bool File()
{
    return
        Optional(Header()) &&
        Body() &&
        Optional(Footer());
}

member.example:
            file = header? body footer?
            

