            alpha ::= [A-Z][a-z]

            num ::= [0-9]

            alpha-num :: alpha | num

            word ::= alpha ( alpha-num )*

            
            Func<char,bool> Alpha = Charset( c =>
                ( 'A' <= c && c <= 'Z' ) ||
                ( 'a' <= c && c <= 'z' ) 
            );
            Func<char,bool> Num = Charset( c =>
                  '0' <= c && c <= '9' 
            );
            Func<char,bool> AlphaNum = Charset( c =>
                Alpha(c) || Num(c)
            );
            bool Word()
            {
                return 
                    Accept(Alpha) &&
                    Repeat(AlphaNum);
                    // No need for RewindUnless
            }

